mapper.select:
	SELECT * FROM 
		(SELECT ROWNUM R, P.* FROM 
			(SELECT * FROM PRODUCT WHERE 
  				C1=#{product.c1}
  				C2=#{product.c2}
  				C3=#{product.c3}
  				AND NAME LIKE '%' || #{pager.search} || '%') P)
  	WHERE R BETWEEN #{pager.startRow} AND #{pager.lastRow}

SELECT COUNT(NUM) FROM PRODUCT WHERE 
C1=#{product.c1}
C2=#{product.c2}
C3=#{product.c3}	
AND NAME LIKE '%' || #{pager.search} || '%'
